import axios from 'axios';

export function getPokemons(){
return async function(dispatch){
  const response = await axios.get('http://localhost:3001/pokemons');
  return dispatch({
    type: 'GET_POKEMONS',
    payload: response.data
  })
}};
export function getTypes(){
  return async function(dispatch){
    const response = await axios.get('http://localhost:3001/types')
    return dispatch({
      type: 'GET_TYPES',
      payload: response.data
    })
  }};
export function getName(name){
  return async function(dispatch){
    try{
      const response = await axios.get(`http://localhost:3001/pokemons?name=${name}`)
      console.log(response.data)
      return dispatch({
        type: 'GET_NAME',
        payload: response.data
      })
    } catch(e){
      alert('Error 404 pokemon not found')
    }
  }};
export function PostPokemon(payload){
  return async function(){
      try{
          const response = await axios.post("http://localhost:3001/pokemons", payload)
          alert('Pokemon created succesfully')
          return response
      }catch(error){
          alert("Error 412: cant create pokemon")
      }
  }};
export function filterPokemonByType(payload){
  return {
      type: "FILTER_POKEMON_BY_TYPE",
      payload
  }};
export function filterPokemonByOfficial(payload){
  return {
      type: "FILTER_POKEMON_BY_OFFICIAL",
      payload
  }};
export function filterPokemonByName(payload){
  return {
      type: "FILTER_POKEMON_BY_NAME",
      payload
  }};
export function sortPokemonByAttack(payload){
  return {
      type: "SORT_POKEMON_BY_ATTACK",
      payload
  }};
export function sortPokemonByWeight(payload){
  return {
    type: 'SORT_POKEMON_BY_WEIGHT',
    payload
  }};
export function getDetail(id) {
  return async function(dispatch){
      try{
      const response = await axios.get(`http://localhost:3001/pokemons/${id}`)
      return dispatch({
          type: "GET_DETAIL",
          payload: response.data
      })
      } catch (error) {
          alert("Error 404: Pokemon info dont found, see if that pokemon exist first.")
      }
  }};
export function removePokemon(id){
  return async function(dispatch){
      try{        
      const response = await axios.delete(`http://localhost:3001/deleted/${id}`);
      return dispatch({
          type: "REMOVE_POKEMON",
          payload: response.data
      })}
      catch(error){
          alert('Error 412: cant delete pokemon')
      }
  }};
export function clearDetail(){
  return {
      type: "CLEAR_DETAIL"
  }};
