import { useState } from "react";
import { useDispatch } from "react-redux";
import style from "./search.module.css";
import { getName } from '../../actions'

export const SearchBar = ({currentPage}) => {
  const dispatch = useDispatch();
  const [name, setName] = useState("");

  const button = style.button;

  const handleChange = (e) => {
    setName(e.target.value);
  };

  const handleSubmit = (e) => {
   e.preventDefault();
    if(name.length > 1){
    dispatch(getName(name))
    setName("");
    setTimeout(() => {
      currentPage(1);
    }, 100);
    }
   else{
    alert("Error 404: not found")
  }
  };

  return (
    <div className={style.container}>
        <input type='text' placeholder='Search...' onChange={(e) => handleChange(e)} value={name} className={style.field}/>
        &nbsp;
        <button type= "submit" onClick={(e) => handleSubmit(e)} className={button}>Search</button>

    </div>
)
};
