import { useState } from "react";
import { useDispatch } from "react-redux";
import style from "./search.module.css";
import { getName } from '../../actions'

export default function SearchBar({currentPage}){
  const dispatch = useDispatch();
  const [name, setName] = useState("");
try{  const handleChange = (e) => {
    setName(e.target.value);
  }

  const handleSubmit = (e) => {
   e.preventDefault();
    dispatch(getName(name))
    setName('');
    setTimeout(() => {currentPage(1)},100);
  }

  return (
    <div className={style.container}>
        <input type='text' placeholder='Search your Pokemon...' onChange={(e) => handleChange(e)} value={name} className={style.field}/>
        &nbsp;
        <button type= "submit" onClick={(e) => handleSubmit(e)} className={style.button}>Search</button>
    </div>
)}
catch(e){
  console.log(e)
  return alert('Error, see console for more details')
}
};
