import React from "react";
import style from "./team.module.css";
import { Card } from "../../components/Cards/Card";


export const Team = () => {

  // const removeTeam = (id) => {
  //   let array = [];
  //   if (localStorage.getItem("Team")) {
  //     array = localStorage.getItem("Team");
  //     array = JSON.parse(array);
  //     array.filter( (e) => e.id != id)
  //     localStorage.setItem("Team", JSON.stringify(array));
  //   } 
  //   history.push("/team");
  // };

  const team = () => {
    if( localStorage.getItem("Team")) {
      let array = localStorage.getItem("Team");
      array = JSON.parse(array);
      return array;
    }
    return []
  }
  const array = team();
  return (
    <div className={style.container}>
      {
        array.length !== 0 ?
      <Card name={array.name} attack={array.attack} Types={array.Types} image={ array.image ? array.image : 'https://media.giphy.com/media/SS4imysASHTHUsXyt6/source.gif?cid=ecf05e47q1uzwaxgylgvwkmsxvhlchbh8kvqe1bmf2emb35d&rid=source.gif&ct=s'}/>
    :
    <div>
      <h2>You dont have any pokemon in your team</h2>
    <img 
    src='https://c.tenor.com/Ir5eG_TuMPYAAAAC/pokemon-confused.gif'
    alt='No-Team'
    />
    </div>
    }
    </div>
  );
};
