import React from "react";
import style from "./team.module.css";
import { Card } from "../../components/Cards/Card";
import { useHistory } from "react-router-dom";


export const Team = () => {
localStorage.clear();
const history = useHistory();

  const removeTeam = (e) => {
    console.log(e.target.id)
    let array = [];
    if (localStorage.getItem("Team")) {
      array = localStorage.getItem("Team");
      array = JSON.parse(array);
      array.filter( (p) => p.id !== e.target.id)
      localStorage.setItem("Team", JSON.stringify(array));
    } 
    history.push("/team");
  };
  const team = () => {
    if( localStorage.getItem("Team")) {
      let array = localStorage.getItem("Team");
      array = JSON.parse(array);
      return array;
    }
    return []
  }
  const array = team();
  return (
    <div className={style.container}>
      {
        array.length !== 0 ?
        <div>
          <button onClick={(e) => removeTeam(e)} className={style.button}>Delete</button>
      <Card id={array.id} name={array.name} attack={array.attack} weight={array.weight} Types={array.Types} image={array.img}/>
      </div>
    :
    <div className={style.noTeam}>
      <br/>
      <h2>You dont have any pokemon in your team</h2>
      <br/>
    <img 
    src='https://c.tenor.com/Ir5eG_TuMPYAAAAC/pokemon-confused.gif'
    alt='No-Team'
    />
    </div>
    }
    </div>
  );
};
