const initialState = {
Pokemons: [],
Types: [],
allPokemons: [],
Details: [],
CreatedPokemons: [],
Team: [],
};

function rootReducer(state = initialState, action){
  switch (action.type) {
    case "GET_POKEMONS":
      return {
        ...state,
        Pokemons: action.payload,
        allPokemons: action.payload,
        CreatedPokemons: action.payload,
      };
    case "GET_TYPES":
        return {
          ...state,
          Types: action.payload,
        };
