          if(a.name > b.name) return 1
          if(a.name < b.name) return -1
          return 0
      }) : filterPokemonByName.sort((a, b) => {
          if(a.name < b.name) return 1
          if(a.name > b.name) return -1
          return 0
      })
      return {
          ...state,
          Pokemons: filterName
      };
    case "SORT_POKEMON_BY_ATTACK":
