import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { Card } from '../Cards/Card.jsx';
import { PostPokemon, getTypes } from "../../actions";
import style from "./form.module.css";


function validate(pokemon){
  const errors = {};
  if(!pokemon.name){
    errors.name = 'Name is required'
  }
  if(pokemon.name.charAt(0) === ' ' || pokemon.name.charAt(0) === '_' || pokemon.name.charAt(0) === '-'){
     errors.name = 'Name must start with a letter'
  }
  if(
    pokemon.name.includes('0') || pokemon.name.includes('1') || pokemon.name.includes('2')
    || pokemon.name.includes('3') || pokemon.name.includes('4') || pokemon.name.includes('5')
    || pokemon.name.includes('6') || pokemon.name.includes('7') || pokemon.name.includes('8')
    || pokemon.name.includes('9')){
      errors.name = 'Name cant include numbers'
    }
  if(
      pokemon.name.includes('!') || pokemon.name.includes('"') || pokemon.name.includes('#')
      || pokemon.name.includes('$') || pokemon.name.includes('%') || pokemon.name.includes('&')
      || pokemon.name.includes('/') || pokemon.name.includes('(') || pokemon.name.includes(')')
      || pokemon.name.includes('=') || pokemon.name.includes('|') || pokemon.name.includes('°')
      || pokemon.name.includes('¬') || pokemon.name.includes('¨') || pokemon.name.includes('*')
      || pokemon.name.includes('[') || pokemon.name.includes(']') || pokemon.name.includes('{')
      || pokemon.name.includes('}')
      ){
        errors.name = 'Name have invalid characters'
    }
  if(
    Number(pokemon.hp) < 0 || Number(pokemon.attack) < 0 || Number(pokemon.defense)< 0 || 
    Number(pokemon.spAtk) < 0 || Number(pokemon.spDef) < 0 || Number(pokemon.speed) < 0 || 
    Number(pokemon.height) < 0 || Number(pokemon.weight) < 0
  ){
    errors.stats = 'Stats can be under 0'
  }
}

export const Form = () => {
  const dispatch = useDispatch();
  const types = useSelector(state => state.Types);
  const history = useHistory ();
  const [data, setData] = useState({
    name: "",
    hp: 1,
    attack: 1,
    defense: 1,
    spAtk: 1,
    spDef: 1,
    speed: 1,
    height: 1,
    weight: 1,
    img: "",
    Types: [],
  });

  const [errors, setErrors] = useState({});
  useEffect(() => {
    dispatch(getTypes());
}, [dispatch]);

  function handleChange(e){
e
  }

  function handleSelect(e){
    const type = e.target.value;
    if(data.Types.length >= 2){return alert('Pokemon can have a maximum of two types')}
    if(!data.Types.includes(type)){
      setData({
        ...data,
        Types: [...data.Types, type]
      })
    }
  }

  function handleSubmit(e){
    e.preventDefault();
    if(!data.name){return alert('Pokemon must have name')}
    dispatch(PostPokemon(data));
    alert('Pokemon created successfully')
    history.push('/home');
  }

function handleDelete(type){
  setData({
    ...data,
    Types: data.Types.filter(t => t !== type)
  })
}

  return (
    <div className={style.containerCreate}>
      <div className={style.form}>
        <div className={style.tittle}>
          <h1>Create your pokemon</h1>
        </div>
        <form onSubmit={(e) => handleSubmit(e)}>
          <div>
            <h3 className= {style.tittle2}>Name</h3>
            <input type='text' value = {data.name} name='name' placeholder="Insert Name" onChanges={(e)=>handleChange(e)} className={style.input} />
            {errors.name && <p className={style.errors}>{errors.name}</p>}
          </div>
          <div>
            <h3 className= {style.tittle2}>HP</h3>
            <input type="number" min='1' max='999' value= {data.hp} name= "hp"  onChange={(e) => handleChange(e)} className= {style.input}/>
            {errors.stats && <p className= {style.errors}>{errors.stats}</p>}
          </div>
          <div>
            <h3 className= {style.tittle2}>Attack</h3>
            <input type="number" min='1' max='999' value= {data.attack} name= "attack" onChange={(e) => handleChange(e)} className= {style.input}/>
            {errors.stats && <p className= {style.errors}>{errors.stats}</p>}
          </div>
          <div>
            <h3 className= {style.tittle2}>Defense</h3>
            <input type="number" min='1' max='999' value= {data.defense} name= "defense" onChange={(e) => handleChange(e)} className= {style.input}/>
            {errors.stats && <p className= {style.errors}>{errors.stats}</p>}
          </div>
          <div>
            <h3 className= {style.tittle2}>Special Attack</h3>
            <input type="number" min='1' max='999' value= {data.spAtk} name= "spAtk" onChange={(e) => handleChange(e)} className= {style.input}/>
            {errors.stats && <p className= {style.errors}>{errors.stats}</p>}
          </div>
          <div>
            <h3 className= {style.tittle2}>Special Defense</h3>
            <input type="number" min='1' max='999' value= {data.spDef} name= "spDef" onChange={(e) => handleChange(e)} className= {style.input}/>
            {errors.stats && <p className= {style.errors}>{errors.stats}</p>}
          </div>
          <div>
            <h3 className= {style.tittle2}>Speed</h3>
            <input type="number" min='1' max='999' value= {data.speed} name= "speed" onChange={(e) => handleChange(e)} className= {style.input}/>
            {errors.stats && <p className= {style.errors}>{errors.stats}</p>}
          </div>
          <div>
            <h3 className= {style.tittle2}>Height</h3>
            <input type="number" min='1' max='999' value= {data.height} name= "height" onChange={(e) => handleChange(e)} className= {style.input}/>
            {errors.stats && <p className= {style.errors}>{errors.stats}</p>}
          </div>
          <div>
            <h3 className= {style.tittle2}>Weight</h3>
            <input type="number" min='1' max='999' value= {data.weight} name= "weight" onChange={(e) => handleChange(e)} className= {style.input}/>
            {errors.stats && <p className= {style.errors}>{errors.stats}</p>}
          </div>
          <div>
            <h3 className= {style.tittle2}>Image</h3>
            <input type="text" value= {data.img} name= "image" placeholder="Insert Image" onChange={(e) => handleChange(e)} className= {style.input}/>
          </div>
          <select onChange={(e) => handleSelect(e)} className= {style.select}>
            <option value= "Select" hidden>Select Type</option>
              {
                types.map(t => (
                  <option key={t.id} value={t.name}>{t.name}</option>
                ))
              }
            </select>
            {Object.keys(errors).length === 0 ? (<div><button type="submit" className= {style.buttonCreate2}>Create Pokemon</button></div>) : (<div><button type="submit" disabled = {true} className= {style.buttonCreate}>Create Pokemon </button></div>)}
        </form>
      </div>
      <div className= {style.card}>
        <Card key={data.id}
          name= {data.name}
          image={data.img}
          Types={data.Types}
          attack= {data.attack}
          weight= {data.weight}
          id = {data.id} />
        </div>
        <div className= {style.types}>
          <h3>Types:</h3> <br/>
        {data.Types.map(el => <div key= {el+Math.random()} className= {style.divtypes}><p>{el}</p><button onClick={() => handleDelete(el)} className= {style.buttonDelete}>Delete</button><br/><br/></div>)}
        </div>
    </div>
  );
};
