import React from 'react';
import {useState, useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {getPokemons, getTypes, filterPokemonByType, filterPokemonByOfficial, filterPokemonByName, sortPokemonByAttack,sortPokemonByWeight} from '../../actions';
import {Link} from 'react-router-dom';
import { Card } from '../../components/Cards/Card.jsx';
import SearchBar from "../../components/Search/Search.jsx";
import style from './pokedex.module.css'

export const Pokedex = () => {
    const dispatch = useDispatch();
    const allPokemons = useSelector(state => state.Pokemons);
    const allTypes = useSelector(state => state.Types);
    const [currentPage, setCurrentPage] = useState(1);
    const [pokemonsPerPage] = useState(8);
    const indexLastPokemon = currentPage * pokemonsPerPage;
    const indexFirstPokemon = indexLastPokemon - pokemonsPerPage;
    const currentPokemons = allPokemons.slice(indexFirstPokemon, indexLastPokemon);
    const paginate = (pageNumber) => setCurrentPage(pageNumber);
    const [order, setOrder] = useState('');
    const [charge, setCharge] = useState(false);

console.log(order)


    useEffect(() => {
        setCharge(true);
        dispatch(getTypes());
        dispatch(getPokemons());
        setTimeout(() => {
            setCharge(false);
        }, 4000);
    }, [dispatch]);

    function handleTypes(e){
        e.preventDefault();
        dispatch(filterPokemonByType(e.target.value));
        setCurrentPage(1);
    }

    function handleCreated(e){
        e.preventDefault();
        dispatch(filterPokemonByOfficial(e.target.value));
        setCurrentPage(1);
        setOrder(e.target.value);
    }

    function handleName(e){
        e.preventDefault();
        dispatch(filterPokemonByName(e.target.value));
        setCurrentPage(1);
        setOrder(e.target.value);
    }

    function handleSortAttack(e){
        e.preventDefault();
        dispatch(sortPokemonByAttack(e.target.value));
        setCurrentPage(1);
        setOrder(e.target.value);
    }

    function handleSortWeight(e){
        e.preventDefault();
        dispatch(sortPokemonByWeight(e.target.value));
        setCurrentPage(1);
        setOrder(e.target.value);
    }
    
    const maxPage = Math.ceil(allPokemons.length / pokemonsPerPage);
    const pages = [];
    for(let i = 1; i <= Math.ceil(allPokemons.length/pokemonsPerPage);i++){
        pages.push(i);
    }

    const handleReset = (e) => {
        e.preventDefault();
        dispatch(getTypes());
        dispatch(getPokemons());
        document.getElementById('order').value = 'order';
        document.getElementById('created').value = 'CREATED';
        document.getElementById('type').value = 'All';
        document.getElementById('attack').value = 'attack';
        document.getElementById('weight').value = 'weight';
        document.getElementById('pages').value = 0;
        setTimeout(()=>{
            setCurrentPage(1);
        }, 1);
    };

    return (
        <div className={style.container}>
                    <SearchBar currentPage = { () => setCurrentPage(1)} />
            <div className={style.inner}>
                <div className={style.top}>
                <button type='submit' onClick={(e) => handleReset(e)} className={style.button}>Reset</button>
                &nbsp;
                <select id='order' defaultValue='order' onChange={e => handleName(e)} className={style.select}>
                    <option value='order' hidden>Order by name</option>
                    <option value='asc'>A - Z</option>
                    <option value='desc'>Z - A</option>
                </select>
                &nbsp;
                <select id='created' defaultValue="CREATED" onChange={e => handleCreated(e)} className= {style.select}>
                    <option value="CREATED" hidden >Filter by create</option>
                    <option value="All">All</option>
                    <option value="api">Official pokemons</option>
                    <option value="fan">Fan pokemons</option>
                </select>
                &nbsp;
                <select id='type' defaultValue = "All" onChange={e => handleTypes(e)} className= {style.select}>
                    <option value="All">Filter by type</option>
                        {
                            allTypes?.map(type => (
                                <option key={type.id} value={type.name} >{type.name}</option>
                            ))
                        }
                </select>
                &nbsp;
                <select id='attack' defaultValue="attack" onChange={e => handleSortAttack(e)} className= {style.select}>
                    <option value="attack" hidden>Order by attack</option>
                    <option value= "attackMin">From weak to strong</option>
                    <option value= "attackMax">From strong to weak</option>
                </select>
                &nbsp;
                <select id='weight' defaultValue="weight" onChange={e => handleSortWeight(e)} className= {style.select}>
                    <option value="weight" hidden>Order by weight</option>
                    <option value= "weightMin">From light to heavy</option>
                    <option value= "weightMax">From heavy to light</option>
                </select>
                </div>
                &nbsp;
                <div className= {style.pg}>
                    <button onClick={()=>{ if(currentPage > 1) paginate(currentPage - 1)}}>
                    &laquo; Previous
                    </button>
                    &nbsp;
                    <span className={style.span}>Page {currentPage} of {maxPage}</span>
                    <button onClick={()=> { if(currentPage < maxPage) paginate(Number(currentPage) + 1)}}>
                        Next &raquo;
                    </button>
                    &nbsp;
                    <select id='pages' defaultValue={0} className= {style.select} onChange={(e) => paginate(e.target.value)} >
                    <option value={0} hidden>Jump to page</option>
                        {pages?.map(n => 
                            <option key={n} value={n}>{n}</option> 
                        )}
                </select>
                </div>
                <div className={style.cardsContainer}>
                    {charge && currentPokemons.length === 0 ?
                        <img src='https://media.baamboozle.com/uploads/images/125978/1629738053_29014_gif-url.gif' alt='loading' />:
                            currentPokemons?.map(pokemon => {
                                return (
                                    <div key={pokemon.id}>
                                        {
                                            <Link to={`/detail/${pokemon.id}`} className={style.link}>
                                                <Card key={pokemon.id}
                                                    name= {pokemon.name}
                                                    image={pokemon.img}
                                                    Types={pokemon.Types[0].name ? pokemon.Types.map(t => t.name) : pokemon.Types.map(t => t + (' '))}
                                                    attack= {pokemon.attack}
                                                    weight= {pokemon.weight}
                                                    id = {pokemon.id} />
                                            </Link>
                                        }
                                    </div>
                                )
                            })
                    }
                    {currentPokemons.length === 0 && !charge ? 
                    <div>
                    <h1><br/><br/><br/>No Pokemons found</h1>
                    </div>
                    : null}
                </div>
            </div>
        </div>
    )
};
